create table ITP_Projekt.t_questions
(
    qu   varchar(100) null,
    q_id int          not null
        primary key
);

create table ITP_Projekt.t_answers
(
    a_id         int         not null
        primary key,
    right_answer tinyint(1)  null,
    answer_text  varchar(80) null,
    q_id         int         null,
    constraint t_answers_ibfk_1
        foreign key (q_id) references ITP_Projekt.t_questions (q_id)
);

create index q_id
    on ITP_Projekt.t_answers (q_id);

create table ITP_Projekt.t_pictures_answers
(
    a_id         int         not null
        primary key,
    right_answer tinyint(1)  null,
    q_id         int         null,
    blob_picture blob        null,
    t_answer     varchar(80) null,
    constraint t_pictures_answers_ibfk_1
        foreign key (q_id) references ITP_Projekt.t_questions (q_id)
);

create index q_id
    on ITP_Projekt.t_pictures_answers (q_id);

